using UnityEngine;using System;using System.Collections;  // UniChordPackedString serialize and deserialize packed string.public class UniChordPackedString{    static readonly string SEPARATOR_REPLACE = "(^_^/";    static readonly string[] SEPARATOR = new string[] { "(*v^/" };    // Android로 부터 받은    // 패킷을 만든다.    public static string SerializePacket(string[] string_array)    {        if (string_array == null || string_array.Length == 0)            return null;        string packet = "";        int count = string_array.Length;        for (int i = 0; i < count; i++)        {            // SEPARATOR와 같은 string은 SEPARATOR_REPLACE로 대체된다.            packet += string_array[i].Replace(SEPARATOR[0], SEPARATOR_REPLACE);            if (i != count - 1)                packet += SEPARATOR[0];        }        return packet;    }    // 패킷을 스트링 배열로 분해한다.    public static string[] DeserializePacket( string packet )    {        if (packet == null)            return null;        string[] string_array = packet.Split(SEPARATOR, StringSplitOptions.None);        return string_array;    }}