using UnityEngine;using System;using System.Collections;using System.Collections.Generic; // This class work instead of IChordManagerListener in unity3d. // You must make child class of this class.// And override functions. public class UniChordManagerListener{    public enum ERROR    {        START_FAIL        = 1000,   // Fail to start.        UNEXPECTED_STOP   = 1001,   // Chord is terminated unexpectedly.    }    public enum REASON    {        STARTED_BY_USER         = 0, // Started by calling start.        STARTED_BY_RECONNECTION = 1, // Started by reconnection    }


    // -------------- Virtual functions about IChordManagerListener ---------------------    // The error is occurred.    public virtual void onError(ERROR error)    {    }    // Called when the network is disconnected.    public virtual void onNetworkDisconnected()    {    }    // Called when the Chord has started successfully.    public virtual void onStarted(string nodeNme, REASON reason)    {    }    // ---------------------------------------------------------------------------------    }